import { gql } from '@apollo/client';

export const GET_CONTACTS = gql`
  {
    contacts {
      id
      firstName
      lastName
    }
  }
`;

export const ADD_CONTACT = `gql
mutation AddContact($id: String!, $firstName: String!, $lastName: String!)
(id: $id, firstName: $firstName, lastName: $lastName)`;
